import logo from './logo.svg';
import './App.css';
import { useEffect, useRef, useState } from 'react';

function App() {

    const[sourcecode,setsourcecode] = useState("");
    useEffect(()=>{
      fetch('code.txt')
      .then((res)=>res.text())
      .then((text)=>setsourcecode(text));
    },[])

    containerref = useRef(null);

    const handlekeychanges = (e) => {
      if(e.key != 'Escape') {
        runscript()
      }
      else {
        removemessage()
      }
    }


  return (
    <div id='container'>
      <div id='source' onKeyDown={handlekeychanges} ref={containerref}>
        Hey I am a hacker
      </div>

    </div>
  );
}

export default App;
